import React, { useState } from "react";

const StoreSelector = ({ stores, selectedStore, onChange, onCreateStore }) => {
    const [isCreating, setIsCreating] = useState(false);
    const [newStoreName, setNewStoreName] = useState("");

    const handleCreateStore = () => {
        if (newStoreName.trim()) {
            onCreateStore(newStoreName);
            setNewStoreName("");
            setIsCreating(false);
        }
    };

    return (
        <div className="store-selector">
            <div className="store-selector-controls">
                <label htmlFor="store-select">Select Store:</label>
                <select 
                    id="store-select"
                    value={selectedStore || ""}
                    onChange={(e) => onChange(e.target.value)}
                >
                    {stores.map(store => (
                        <option key={store.id} value={store.id}>{store.name}</option>
                    ))}
                </select>
                
                <button 
                    className="create-store-btn"
                    onClick={() => setIsCreating(!isCreating)}
                >
                    {isCreating ? "Cancel" : "Create Store"}
                </button>
            </div>

            {isCreating && (
                <div className="create-store-form">
                    <input
                        type="text"
                        placeholder="New Store Name"
                        value={newStoreName}
                        onChange={(e) => setNewStoreName(e.target.value)}
                    />
                    <button onClick={handleCreateStore}>Save</button>
                </div>
            )}
        </div>
    );
};

export default StoreSelector;
