// StoreOrganizer.js
import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragOverlay
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  useSortable,
  verticalListSortingStrategy
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

const SortableIngredient = ({ ingredient, index }) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging
  } = useSortable({
    id: `ingredient-${ingredient.id}`,
    data: { ingredient }
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.4 : 1,
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      className={`ingredient-item ${isDragging ? 'dragging' : ''}`}
      {...attributes}
      {...listeners}
    >
      {ingredient.name}
    </div>
  );
};

const StoreOrganizer = () => {
  const navigate = useNavigate();
  const [sections, setSections] = useState([]);
  const [, setIngredients] = useState([]);
  const [stores, setStores] = useState([]);
  const [selectedStore, setSelectedStore] = useState(null);
  const [newSectionName, setNewSectionName] = useState("");
  const [loading, setLoading] = useState(true);
  const [saveSuccess, setSaveSuccess] = useState(false);
  const [activeId, setActiveId] = useState(null);
  const [activeDragData, setActiveDragData] = useState(null);

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Fetch sections function
  const fetchSections = useCallback(async (storeId) => {
    try {
      const sectionsResponse = await fetch(`http://127.0.0.1:5000/api/stores/${storeId}/sections`);
      const sectionsData = await sectionsResponse.json();

      // Initialize sections with empty ingredient arrays
      const sectionsWithIngredients = sectionsData.map(section => ({
        ...section,
        ingredients: []
      }));

      setSections(sectionsWithIngredients);

      // Fetch ingredients
      const ingredientsResponse = await fetch("http://127.0.0.1:5000/api/ingredients");
      const ingredientsData = await ingredientsResponse.json();

      if (ingredientsData.length === 0) {
        setSections(sectionsWithIngredients);
        setIngredients([]);
        setLoading(false);
        return; // Early return if no ingredients to process
      }

      // Fetch ingredient section mappings
      const mappingsResponse = await fetch(`http://127.0.0.1:5000/api/ingredient_sections?store_id=${storeId}`);
      const mappingsData = await mappingsResponse.json();

      // Organize ingredients into sections
      const sectionMap = {};
      sectionsWithIngredients.forEach(section => {
        sectionMap[section.id] = section;
      });

      // Add a default "Uncategorized" section
      let uncategorizedSection = {
        id: "uncategorized",
        name: "Uncategorized",
        ingredients: []
      };

      // Assign ingredients to their sections based on mappings
      ingredientsData.forEach(ingredient => {
        const mapping = mappingsData.find(m => m.ingredient_id === ingredient.id);

        if (mapping && sectionMap[mapping.section_id]) {
          sectionMap[mapping.section_id].ingredients.push(ingredient);
        } else {
          uncategorizedSection.ingredients.push(ingredient);
        }
      });

      // Update sections array with assigned ingredients
      const updatedSections = [...Object.values(sectionMap)];

      // Add uncategorized section if it has ingredients
      if (uncategorizedSection.ingredients.length > 0) {
        updatedSections.push(uncategorizedSection);
      }

      setSections(updatedSections);
      setIngredients(ingredientsData);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching sections and ingredients:", error);
      setLoading(false);
    }
  }, []);

  // Define fetchData after fetchSections
  const fetchData = useCallback(async () => {
    try {
      setLoading(true);

      // Fetch stores
      const storesResponse = await fetch("http://127.0.0.1:5000/api/stores");
      const storesData = await storesResponse.json();
      setStores(storesData);

      if (storesData.length > 0) {
        setSelectedStore(storesData[0].id);

        // Fetch sections for the selected store
        await fetchSections(storesData[0].id);
      } else {
        // Create a default store if none exists
        await createDefaultStore();
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  }, [fetchSections, createDefaultStore]);


  // Move this function up in the file, right after the fetchSections function
  const createDefaultStore = useCallback(async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/stores", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          name: "My Store",
          sections: [
            { name: "Produce", order: 0 },
            { name: "Dairy", order: 1 },
            { name: "Meat", order: 2 },
            { name: "Bakery", order: 3 },
            { name: "Frozen", order: 4 },
            { name: "Canned Goods", order: 5 },
            { name: "Uncategorized", order: 6 }
          ]
        })
      });

      const data = await response.json();

      if (data.store_id) {
        // Fetch stores again to get the new store
        const storesResponse = await fetch("http://127.0.0.1:5000/api/stores");
        const storesData = await storesResponse.json();
        setStores(storesData);

        // Set the first store as selected
        if (storesData.length > 0) {
          setSelectedStore(storesData[0].id);
          await fetchSections(storesData[0].id);
        }
      }

      setLoading(false);
    } catch (error) {
      console.error("Error creating default store:", error);
      setLoading(false);
    }
  }, [fetchSections]);

  // Use fetchData in useEffect
  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleStoreChange = (e) => {
    const storeId = e.target.value;
    setSelectedStore(storeId);
    fetchSections(storeId);
  };

  const addNewSection = () => {
    if (!newSectionName.trim()) {
      alert("Please enter a section name");
      return;
    }

    // Create new section locally first
    const newSection = {
      id: `temp-${Date.now()}`, // Temporary ID until saved
      name: newSectionName,
      order: sections.length,
      ingredients: []
    };

    setSections([...sections, newSection]);
    setNewSectionName("");
  };

  const removeSection = (sectionId) => {
    // Get the section
    const section = sections.find(s => s.id.toString() === sectionId.toString());

    if (!section) return;

    // If section has ingredients, move them to uncategorized
    let uncategorized = sections.find(s => s.id === "uncategorized");

    if (section.ingredients.length > 0) {
      if (!uncategorized) {
        // Create uncategorized section if it doesn't exist
        uncategorized = {
          id: "uncategorized",
          name: "Uncategorized",
          ingredients: []
        };

        // Add to sections
        setSections([...sections.filter(s => s.id.toString() !== sectionId.toString()), uncategorized]);
      } else {
        // Update sections without the removed one
        setSections(sections.filter(s => s.id.toString() !== sectionId.toString()));
      }

      // Move ingredients to uncategorized
      uncategorized.ingredients = [...uncategorized.ingredients, ...section.ingredients];
    } else {
      // Just remove the section
      setSections(sections.filter(s => s.id.toString() !== sectionId.toString()));
    }
  };

  const saveOrganization = async () => {
    try {
      // Prepare the data
      const sectionData = sections.map(section => ({
        id: section.id === "uncategorized" ? null : section.id,
        name: section.name,
        ingredients: section.ingredients.map((ingredient, index) => ({
          id: ingredient.id,
          name: ingredient.name,
          order: index
        }))
      }));

      // Send to the backend
      const response = await fetch("http://127.0.0.1:5000/api/save_ingredient_sections", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          store_id: selectedStore,
          sections: sectionData
        })
      });

      if (!response.ok) {
        throw new Error("Failed to save organization");
      }

      // Show success message
      setSaveSuccess(true);
      setTimeout(() => setSaveSuccess(false), 3000);

    } catch (error) {
      console.error("Error saving organization:", error);
      alert("Error saving organization: " + error.message);
    }
  };

  const handleDragStart = (event) => {
    const { active } = event;
    setActiveId(active.id);
    setActiveDragData(active.data.current?.ingredient || null);
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;

    if (!active || !over) return;

    // Find the containers (sections)
    const activeContainer = findContainer(active.id);
    const overContainer = findContainer(over.id);

    // If we're just dropping back where we started, return
    if (
      activeContainer === overContainer &&
      active.id === over.id
    ) {
      return;
    }

    // Handle the case where we're moving an item within the same section
    if (activeContainer === overContainer) {
      const activeIndex = findIndex(activeContainer, active.id);
      const overIndex = findIndex(overContainer, over.id);

      if (activeIndex !== overIndex) {
        setSections(prev => {
          const newSections = [...prev];
          const sectionIndex = newSections.findIndex(s => s.id.toString() === activeContainer);
          if (sectionIndex !== -1) {
            const newIngredients = [...newSections[sectionIndex].ingredients];
            newSections[sectionIndex].ingredients = arrayMove(
              newIngredients,
              activeIndex,
              overIndex
            );
          }
          return newSections;
        });
      }
    }
    // Handle the case where we're moving an item to a different section
    else if (activeContainer !== overContainer) {
      const activeIngredient = getIngredientById(active.id);

      if (!activeIngredient) return;

      setSections(prev => {
        const newSections = [...prev];

        const activeSectionId = activeContainer.replace('section-', '');
        const activeSectionIndex = newSections.findIndex(s => s.id.toString() === activeSectionId);
        if (activeSectionIndex !== -1) {
          const activeIngredientId = active.id.replace('ingredient-', '');
          newSections[activeSectionIndex].ingredients = newSections[activeSectionIndex].ingredients.filter(
            i => i.id.toString() !== activeIngredientId
          );
        }

        const overSectionId = overContainer.replace('section-', '');
        const overSectionIndex = newSections.findIndex(s => s.id.toString() === overSectionId);
        if (overSectionIndex !== -1) {
          // Find the position to insert at
          const overItemId = over.id;
          const overItemIndex = findIndex(overContainer, overItemId);

          // Insert the dragged ingredient at the new position
          const newIngredients = [...newSections[overSectionIndex].ingredients];
          newIngredients.splice(overItemIndex, 0, activeIngredient);
          newSections[overSectionIndex].ingredients = newIngredients;
        }

        return newSections;
      });
    }

    setActiveId(null);
    setActiveDragData(null);
  };

  // Helper function to find the container (section) an item belongs to
  const findContainer = (id) => {
    // Check if this is a section ID (sections are directly draggable too)
    const sectionIdMatch = sections.find(section =>
      `section-${section.id}` === id
    );
    if (sectionIdMatch) {
      return `section-${sectionIdMatch.id}`;
    }

    // Check if this is an ingredient ID
    if (id.startsWith('ingredient-')) {
      const ingredientId = id.replace('ingredient-', '');
      for (const section of sections) {
        const foundIngredient = section.ingredients.find(ingredient =>
          ingredient.id.toString() === ingredientId
        );
        if (foundIngredient) {
          return `section-${section.id}`;
        }
      }
    }

    return null;
  };

  // Helper function to find an ingredient's index within a section
  const findIndex = (containerId, itemId) => {
    const sectionId = containerId.replace('section-', '');
    const section = sections.find(s => s.id.toString() === sectionId);
    if (!section) return -1;

    if (itemId.startsWith('ingredient-')) {
      const ingredientId = itemId.replace('ingredient-', '');
      return section.ingredients.findIndex(i => i.id.toString() === ingredientId);
    }

    return -1;
  };

  // Helper function to get an ingredient by ID
  const getIngredientById = (id) => {
    if (id.startsWith('ingredient-')) {
      const ingredientId = id.replace('ingredient-', '');
      for (const section of sections) {
        const ingredient = section.ingredients.find(i => i.id.toString() === ingredientId);
        if (ingredient) return ingredient;
      }
    }
    return null;
  };

  return (
    <div className="store-organizer-container">
      <div className="store-organizer-header">
        <h1>Store Section Organizer</h1>
        {saveSuccess && <div className="save-success-message">Organization saved successfully!</div>}
      </div>

      <div className="store-organizer-controls">
        <button
          className="back-btn"
          onClick={() => navigate('/grocery-list')}
        >
          ← Back to Grocery List
        </button>

        <div className="store-selector">
          <label htmlFor="store-select">Select Store:</label>
          <select
            id="store-select"
            value={selectedStore || ""}
            onChange={handleStoreChange}
          >
            {stores.map(store => (
              <option key={store.id} value={store.id}>{store.name}</option>
            ))}
          </select>
        </div>

        <div className="add-section-form">
          <input
            type="text"
            value={newSectionName}
            onChange={(e) => setNewSectionName(e.target.value)}
            placeholder="New Section Name"
          />
          <button onClick={addNewSection}>Add Section</button>
        </div>

        <button className="save-organization-btn" onClick={saveOrganization}>
          Save Organization
        </button>
      </div>

      {loading ? (
        <div className="loading">Loading sections and ingredients...</div>
      ) : (
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
        >
          <div className="sections-container">
            {sections.map(section => (
              <div className="section-card" key={section.id}>
                <div className="section-header">
                  <h3>{section.name}</h3>
                  {section.id !== "uncategorized" && (
                    <button
                      className="remove-section-btn"
                      onClick={() => removeSection(section.id)}
                    >
                      ✕
                    </button>
                  )}
                </div>

                <SortableContext
                  id={`section-${section.id}`}
                  items={section.ingredients.map(i => `ingredient-${i.id}`)}
                  strategy={verticalListSortingStrategy}
                >
                  <div className="ingredient-list">
                    {section.ingredients.length > 0 ? (
                      section.ingredients.map((ingredient, index) => (
                        <SortableIngredient
                          key={ingredient.id}
                          ingredient={ingredient}
                          index={index}
                        />
                      ))
                    ) : (
                      <div className="empty-section">
                        Drag ingredients here
                      </div>
                    )}
                  </div>
                </SortableContext>
              </div>
            ))}
          </div>

          <DragOverlay>
            {activeId && activeDragData ? (
              <div className="ingredient-item dragging">
                {activeDragData.name}
              </div>
            ) : null}
          </DragOverlay>
        </DndContext>
      )}

      <div className="organizer-tip">
        <p>Tip: Drag and drop ingredients between sections to organize your grocery list.</p>
      </div>
    </div>
  );
};

export default StoreOrganizer;
