import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import RecipeTreeView from "../pages/RecipeTreeView";
import './RecipeManagement.css';

function RecipeManagement() {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [expandedSubRecipes, setExpandedSubRecipes] = useState({});
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("name-asc");
  const [subRecipes, setSubRecipes] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    fetchRecipes();
  }, []);

  // Fetch all recipes
  const fetchRecipes = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/recipes");
      const data = await response.json();
      setRecipes(data);
    } catch (error) {
      console.error("Error fetching recipes:", error);
    }
  };

  // Fetch a single recipe with details
  const fetchRecipeDetails = async (recipeId) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/recipes/${recipeId}`);
      const data = await response.json();
      setSelectedRecipe(data);
    } catch (error) {
      console.error("Error fetching recipe details:", error);
    }
  };

  // Toggle sub-recipe expansion
  const toggleSubRecipe = async (subRecipeId) => {
    setExpandedSubRecipes(prevState => ({
      ...prevState,
      [subRecipeId]: !prevState[subRecipeId], // Toggle open/closed state
    }));

    // Only fetch if not already loaded or if we're expanding
    if (!subRecipes[subRecipeId] && !expandedSubRecipes[subRecipeId]) {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/recipes/${subRecipeId}`);
        const data = await response.json();
        setSubRecipes(prev => ({
          ...prev,
          [subRecipeId]: data
        }));
      } catch (error) {
        console.error("Error loading sub-recipe:", error);
      }
    }
  };

  // Delete recipe
  const handleDelete = async (recipeId) => {
    if (!window.confirm("Are you sure you want to delete this recipe?")) return;

    try {
      const response = await fetch(`http://127.0.0.1:5000/api/recipes/${recipeId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete recipe");

      // Clear selected recipe if it's the one we deleted
      if (selectedRecipe && selectedRecipe.id === recipeId) {
        setSelectedRecipe(null);
      }

      fetchRecipes(); // Refresh list after deletion
    } catch (error) {
      console.error("Error deleting recipe:", error);
    }
  };

  // Toggle fullscreen mode
  const toggleFullscreen = () => {
    setIsFullscreen(!isFullscreen);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handle sort order change
  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
  };

  // Sort and filter recipes
  const filteredAndSortedRecipes = React.useMemo(() => {
    // First filter
    const filtered = recipes.filter(recipe => 
      recipe.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Then sort
    return filtered.sort((a, b) => {
      switch(sortOrder) {
        case "name-asc":
          return a.name.localeCompare(b.name);
        case "name-desc":
          return b.name.localeCompare(a.name);
        // Add more sort options as needed
        default:
          return 0;
      }
    });
  }, [recipes, searchTerm, sortOrder]);

  // Render sub-recipe tree (recursive function)
  const renderSubRecipeTree = (recipe, level = 0) => {
    if (!recipe) return null;

    return (
      <div className="sub-recipe-item" style={{ marginLeft: `${level * 20}px` }}>
        <div className="sub-recipe-header">
          <h4>{recipe.name}</h4>
        </div>
        
        {recipe.ingredients && recipe.ingredients.length > 0 && (
          <div className="sub-recipe-ingredients">
            <h5>Ingredients:</h5>
            <ul>
              {recipe.ingredients.map((ingredient, idx) => (
                <li key={idx}>
                  {ingredient.quantity} {ingredient.unit} {ingredient.ingredient?.name || "N/A"}
                </li>
              ))}
            </ul>
          </div>
        )}

        {recipe.components && recipe.components.length > 0 && (
          <div className="nested-sub-recipes">
            <h5>Sub-recipes:</h5>
            {recipe.components.map((component, idx) => {
              const isExpanded = expandedSubRecipes[component.sub_recipe?.id];
              const subRecipeDetails = subRecipes[component.sub_recipe?.id];
              
              return (
                <div key={idx} className="nested-sub-recipe">
                  <div 
                    className="sub-recipe-toggle"
                    onClick={() => toggleSubRecipe(component.sub_recipe?.id)}
                  >
                    <span className="toggle-icon">
                      {isExpanded ? '‚ñº' : '‚ñ∫'}
                    </span>
                    <span className="sub-recipe-name">
                      {component.quantity} {component.sub_recipe?.name}
                    </span>
                  </div>
                  
                  {isExpanded && subRecipeDetails && (
                    <div className="expanded-sub-recipe">
                      {renderSubRecipeTree(subRecipeDetails, level + 1)}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className={`recipe-management ${isFullscreen ? 'fullscreen' : ''}`}>
      <div className="recipe-management-header">
        <h2>Recipe Management</h2>
        <div className="recipe-controls">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search recipes..."
              value={searchTerm}
              onChange={handleSearchChange}
              className="search-input"
            />
          </div>
          <div className="sort-container">
            <select 
              value={sortOrder}
              onChange={handleSortChange}
              className="sort-select"
            >
              <option value="name-asc">Name (A-Z)</option>
              <option value="name-desc">Name (Z-A)</option>
              {/* Add more sort options as needed */}
            </select>
          </div>
        </div>
      </div>

      <div className="recipe-management-content">
        {/* Recipe List (left panel) */}
        <div className={`recipe-list-panel ${selectedRecipe && isFullscreen ? 'hidden' : ''}`}>
          <h3>Recipes</h3>
          {filteredAndSortedRecipes.length > 0 ? (
            <ul className="recipe-list">
              {filteredAndSortedRecipes.map((recipe) => (
                <li 
                  key={recipe.id} 
                  className={`recipe-item ${selectedRecipe?.id === recipe.id ? 'selected' : ''}`}
                  onClick={() => fetchRecipeDetails(recipe.id)}
                >
                  <span className="recipe-name">{recipe.name}</span>
                  <div className="recipe-actions">
                    <button 
                      className="edit-btn" 
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate(`/recipes/${recipe.id}/edit`);
                      }}
                    >
                      ‚úèÔ∏è
                    </button>
                    <button 
                      className="delete-btn" 
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDelete(recipe.id);
                      }}
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No recipes found. Try adjusting your search or add a new recipe.</p>
          )}
        </div>

        {/* Recipe Details (right panel) */}
        <div className={`recipe-detail-panel ${selectedRecipe ? '' : 'empty'} ${isFullscreen ? 'fullscreen' : ''}`}>
          {selectedRecipe ? (
            <div className="recipe-detail">
              <div className="recipe-detail-header">
                <h3>{selectedRecipe.name}</h3>
                <div className="recipe-detail-actions">
                  <button 
                    className="fullscreen-btn" 
                    onClick={toggleFullscreen}
                  >
                    {isFullscreen ? '‚äû' : '‚õ∂'}
                  </button>
                  <button 
                    className="edit-btn" 
                    onClick={() => navigate(`/recipes/${selectedRecipe.id}/edit`)}
                  >
                    Edit
                  </button>
                  <button 
                    className="delete-btn" 
                    onClick={() => handleDelete(selectedRecipe.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
              
              <div className="recipe-detail-info">
                <p><strong>Cook Time:</strong> {selectedRecipe.cook_time || "N/A"} minutes</p>
                <p><strong>Servings:</strong> {selectedRecipe.servings || "N/A"}</p>
              </div>
              
              <div className="recipe-detail-ingredients">
                <h4>Ingredients:</h4>
                {selectedRecipe.ingredients && selectedRecipe.ingredients.length > 0 ? (
                  <ul className="ingredients-list">
                    {selectedRecipe.ingredients.map((ingredient, index) => (
                      <li key={index}>
                        {ingredient.quantity} {ingredient.unit} {ingredient.ingredient?.name || "N/A"}
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p>No ingredients listed.</p>
                )}
              </div>
              
              {/* Sub-recipes section */}
              {selectedRecipe.components && selectedRecipe.components.length > 0 && (
                <div className="recipe-detail-sub-recipes">
                  <h4>Sub-recipes:</h4>
                  <div className="sub-recipes-tree">
                    <RecipeTreeView recipe={selectedRecipe} initialExpanded={true} />
                  </div>
                </div>
              )}
              
              <div className="recipe-detail-instructions">
                <h4>Instructions:</h4>
                <p>{selectedRecipe.instructions || "No instructions available."}</p>
              </div>
            </div>
          ) : (
            <div className="no-recipe-selected">
              <p>Select a recipe from the list to view details</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default RecipeManagement;
