import React, { useEffect, useState } from "react";

function Recipes() {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const toggleSubRecipe = async (subRecipeId) => {
    const subRecipeList = document.getElementById(`sub-recipe-${subRecipeId}`);
  
    if (subRecipeList.style.display === "none") {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/recipes/${subRecipeId}`);
        const subRecipe = await response.json();
  
        subRecipeList.innerHTML = subRecipe.ingredients.map((subIngredient) => `
          <li>${subIngredient.quantity} ${subIngredient.unit} - ${subIngredient.ingredient.name}</li>
        `).join("");
  
        subRecipeList.style.display = "block";
      } catch (error) {
        console.error("Error loading sub-recipe:", error);
      }
    } else {
      subRecipeList.style.display = "none";
    }
  };
  

  // Fetch recipes from backend
  useEffect(() => {
    async function fetchData() {
      try {
        // Fetch recipes
        const recipesResponse = await fetch("http://127.0.0.1:5000/api/recipes");
        const recipesData = await recipesResponse.json();
  
        // Fetch sub-recipes
        const subRecipesResponse = await fetch("http://127.0.0.1:5000/api/sub_recipes");
        const subRecipesData = await subRecipesResponse.json();
  
        console.log("Fetched Recipes:", recipesData);
        console.log("Fetched Sub-Recipes:", subRecipesData);
  
        setRecipes(recipesData);
        setSubRecipes(subRecipesData); // Store sub-recipes separately
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    }
  
    fetchData();
  }, []);
  
  

  // Handle recipe selection
  const handleSelectRecipe = (e) => {
    const recipeId = e.target.value;
    if (recipeId) {
      fetch(`http://127.0.0.1:5000/api/recipes/${recipeId}`)
        .then((response) => response.json())
        .then((data) => setSelectedRecipe(data))
        .catch((error) => console.error("Error fetching recipe details:", error));
    } else {
      setSelectedRecipe(null);
    }
  };

  return (
    <div>
      <h1>Recipes</h1>
      <label htmlFor="recipeDropdown">Choose a Recipe:</label>
      <select id="recipeDropdown" onChange={handleSelectRecipe}>
  <option value="">-- Select a Recipe --</option>

  {/* Regular Recipes */}
  {recipes.length > 0 && (
    <>
      <option disabled>-- Recipes --</option>
      {recipes.map((recipe) => (
        <option key={recipe.id} value={recipe.id}>
          {recipe.name}
        </option>
      ))}
    </>
  )}

  {/* Sub-Recipes Section */}
  {subRecipes.length > 0 && (
    <>
      <option disabled>-- Sub-Recipes --</option>
      {subRecipes.map((subRecipe) => (
        <option key={subRecipe.id} value={subRecipe.id}>
          {subRecipe.name} (Sub)
        </option>
      ))}
    </>
  )}
</select>


      {selectedRecipe && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h2>{selectedRecipe.name}</h2>
          <p><strong>Cook Time:</strong> {selectedRecipe.cook_time || "N/A"} minutes</p>
          <p><strong>Servings:</strong> {selectedRecipe.servings || "N/A"}</p>
          <h3>Ingredients:</h3>
          <ul>
  {selectedRecipe.ingredients.length > 0 ? (
    selectedRecipe.ingredients.map((ingredient, index) => {
      if (ingredient.ingredient.used_in_recipes && ingredient.ingredient.used_in_recipes.length > 0) {
        return (
          <li key={index} className="sub-recipe" onClick={() => toggleSubRecipe(ingredient.ingredient.id)}>
            <span style={{ color: "green", cursor: "pointer" }}>
              {ingredient.ingredient.name} (Sub-Recipe) ⬇
            </span>
            <ul id={`sub-recipe-${ingredient.ingredient.id}`} style={{ display: "none" }}></ul>
          </li>
        );
      } else {
        return (
          <li key={index}>
            {ingredient.quantity} {ingredient.unit} - {ingredient.ingredient.name}
          </li>
        );
      }
    })
  ) : (
    <li>No ingredients listed.</li>
  )}
</ul>

          <h3>Instructions:</h3>
          <p>{selectedRecipe.instructions || "No instructions available."}</p>
        </div>
      )}
    </div>
  );
}

export default Recipes;
