import React, { useState, useEffect, useCallback } from 'react';

function RecipeTreeView({ recipe, initialExpanded = false, level = 0, visitedIds = new Set() }) {
  const [expanded, setExpanded] = useState(initialExpanded);
  const [subRecipeData, setSubRecipeData] = useState({});
  const [loadingSubRecipes, setLoadingSubRecipes] = useState({});
  
  // Memoize fetchSubRecipe to prevent infinite loop in useEffect - must be defined before conditional returns
  const fetchSubRecipe = useCallback(async (subRecipeId) => {
    if (loadingSubRecipes[subRecipeId]) return;

    setLoadingSubRecipes(prev => ({ ...prev, [subRecipeId]: true }));

    try {
      const response = await fetch(`http://127.0.0.1:5000/api/recipes/${subRecipeId}`);
      const data = await response.json();
      
      setSubRecipeData(prev => ({
        ...prev,
        [subRecipeId]: data
      }));
    } catch (error) {
      console.error(`Error loading sub-recipe ${subRecipeId}:`, error);
    } finally {
      setLoadingSubRecipes(prev => ({ ...prev, [subRecipeId]: false }));
    }
  }, [loadingSubRecipes]);

  // useEffect must also be before any conditional returns
  useEffect(() => {
    // If expanded initially, load any immediate sub-recipes
    if (initialExpanded && recipe && recipe.components) {
      recipe.components.forEach((component) => {
        if (component.sub_recipe && component.sub_recipe.id) {
          fetchSubRecipe(component.sub_recipe.id);
        }
      });
    }
  }, [initialExpanded, recipe, fetchSubRecipe]);

  const toggleExpand = () => {
    setExpanded(!expanded);

    // If expanding and we have sub-recipes to load, fetch them
    if (!expanded && recipe && recipe.components) {
      recipe.components.forEach((component) => {
        if (
          component.sub_recipe && 
          component.sub_recipe.id && 
          !subRecipeData[component.sub_recipe.id]
        ) {
          fetchSubRecipe(component.sub_recipe.id);
        }
      });
    }
  };
  
  // Prevent infinite recursion by checking if we've already visited this recipe
  if (recipe && recipe.id && visitedIds.has(recipe.id)) {
    return (
      <div className="recipe-tree-node recursive-warning" style={{ marginLeft: `${level * 16}px` }}>
        <div className="recipe-tree-header">
          <span className="recipe-tree-warning">⚠️</span>
          <span className="recipe-tree-name">{recipe.name} (recursive reference)</span>
        </div>
      </div>
    );
  }
  
  // Add this ID to visited set for child components
  const newVisitedIds = new Set(visitedIds);
  if (recipe && recipe.id) {
    newVisitedIds.add(recipe.id);
  }

  if (!recipe) return null;

  // Determine if this recipe has sub-recipes
  const hasSubRecipes = recipe.components && recipe.components.length > 0;

  return (
    <div className="recipe-tree-node" style={{ marginLeft: `${level * 16}px` }}>
      <div 
        className={`recipe-tree-header ${hasSubRecipes ? 'has-children' : ''}`}
        onClick={hasSubRecipes ? toggleExpand : undefined}
      >
        {hasSubRecipes && (
          <span className="recipe-tree-toggle">
            {expanded ? '▼' : '►'}
          </span>
        )}
        <span className="recipe-tree-name">{recipe.name}</span>
      </div>

      {expanded && (
        <div className="recipe-tree-content">
          {/* Ingredients */}
          <div className="recipe-tree-ingredients">
            <h5>Ingredients:</h5>
            <ul>
              {recipe.ingredients && recipe.ingredients.map((ingredient, idx) => (
                <li key={idx}>
                  {ingredient.quantity} {ingredient.unit} {ingredient.ingredient?.name || "N/A"}
                </li>
              ))}
            </ul>
          </div>

          {/* Sub-recipes */}
          {hasSubRecipes && (
            <div className="recipe-tree-sub-recipes">
              <h5>Sub-recipes:</h5>
              {recipe.components.map((component, idx) => {
                const subRecipe = subRecipeData[component.sub_recipe?.id];
                const isLoading = loadingSubRecipes[component.sub_recipe?.id];

                return (
                  <div key={idx} className="recipe-tree-sub-recipe">
                    <div className="recipe-tree-sub-recipe-header">
                      <span className="recipe-tree-quantity">
                        {component.quantity} 
                      </span>
                      {isLoading ? (
                        <span className="recipe-tree-loading">Loading...</span>
                      ) : subRecipe ? (
                        <RecipeTreeView 
                          recipe={subRecipe} 
                          level={level + 1}
                          visitedIds={newVisitedIds}
                        />
                      ) : (
                        <span 
                          className="recipe-tree-load" 
                          onClick={() => fetchSubRecipe(component.sub_recipe?.id)}
                        >
                          {component.sub_recipe?.name} (Click to load)
                        </span>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default RecipeTreeView;
