import React, { useEffect, useState } from "react";

function Recipes() {
  const [recipes, setRecipes] = useState([]);
  const [subRecipes, setSubRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [expandedSubRecipes, setExpandedSubRecipes] = useState({});
  const [editingRecipeId, setEditingRecipeId] = useState(null);

  const [recipeForm, setRecipeForm] = useState({
    name: "",
    cook_time: "",
    servings: "",
    instructions: "",
    parentRecipeId: "",
    ingredients: [],
  });

  // Fetch all recipes
  const fetchRecipes = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/recipes");
      const data = await response.json();
      setRecipes(data);
    } catch (error) {
      console.error("Error fetching recipes:", error);
    }
  };

  // Fetch all sub-recipes
  const fetchSubRecipes = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/sub_recipes");
      const data = await response.json();
      setSubRecipes(data);
    } catch (error) {
      console.error("Error fetching sub-recipes:", error);
    }
  };

  // Expand/collapse sub-recipe
  const toggleSubRecipe = async (subRecipeId) => {
    setExpandedSubRecipes((prevState) => ({
      ...prevState,
      [subRecipeId]: !prevState[subRecipeId],
    }));

    if (!expandedSubRecipes[subRecipeId]) {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/sub_recipes/${subRecipeId}`);
        const subRecipe = await response.json();

        setSubRecipes((prev) => ({
          ...prev,
          [subRecipeId]: subRecipe.ingredients,
        }));
      } catch (error) {
        console.error("Error loading sub-recipe:", error);
      }
    }
  };

  useEffect(() => {
    fetchRecipes();
    fetchSubRecipes();
  }, []);

  // Handle input changes
  const handleInputChange = (e) => {
    setRecipeForm({ ...recipeForm, [e.target.name]: e.target.value });
  };

  // ✅ Add a new ingredient with full structure
  const addIngredient = () => {
    setRecipeForm((prevForm) => ({
      ...prevForm,
      ingredients: [
        ...prevForm.ingredients,
        {
          quantity: "",
          unit: "",
          size: "",
          descriptor: "",
          name: "",
          additional_descriptor: "",
        },
      ],
    }));
  };

  // ✅ Handle ingredient field changes
  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...recipeForm.ingredients];
    updatedIngredients[index][field] = value;
    setRecipeForm({ ...recipeForm, ingredients: updatedIngredients });
  };

  // ✅ Remove an ingredient
  const removeIngredient = (index) => {
    const updatedIngredients = recipeForm.ingredients.filter((_, i) => i !== index);
    setRecipeForm({ ...recipeForm, ingredients: updatedIngredients });
  };

  // ✅ Submit form (Create or Update Recipe)
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const method = editingRecipeId ? "PUT" : "POST";
    const url = editingRecipeId
      ? `http://127.0.0.1:5000/api/recipes/${editingRecipeId}`
      : "http://127.0.0.1:5000/api/recipes";

    try {
      const response = await fetch(url, {
        method: method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(recipeForm),
      });

      if (!response.ok) throw new Error("Failed to save recipe");

      alert(editingRecipeId ? "Recipe updated!" : "Recipe added!");
      setRecipeForm({ name: "", cook_time: "", servings: "", instructions: "", ingredients: [] });
      setEditingRecipeId(null);
      fetchRecipes();
    } catch (error) {
      console.error("Error saving recipe:", error);
    }
  };

  return (
    <div>
      <h1>Recipes</h1>
      <h2>{editingRecipeId ? "Edit Recipe" : "Add a New Recipe"}</h2>

      <form onSubmit={handleFormSubmit}>
        <input type="text" name="name" placeholder="Recipe Name" value={recipeForm.name} onChange={handleInputChange} required />
        <input type="number" name="cook_time" placeholder="Cook Time (minutes)" value={recipeForm.cook_time} onChange={handleInputChange} />
        <input type="number" name="servings" placeholder="Servings" value={recipeForm.servings} onChange={handleInputChange} />

        <h3>Ingredients</h3>
        {recipeForm.ingredients.map((ingredient, index) => (
          <div key={index} style={{ display: "flex", gap: "10px", marginBottom: "5px" }}>
            <input type="text" placeholder="Quantity" value={ingredient.quantity} onChange={(e) => handleIngredientChange(index, "quantity", e.target.value)} />
            <select value={ingredient.unit} onChange={(e) => handleIngredientChange(index, "unit", e.target.value)}>
              <option value="">Unit</option>
              <option value="g">Grams</option>
              <option value="ml">Milliliters</option>
              <option value="cup">Cups</option>
              <option value="tbsp">Tablespoons</option>
              <option value="tsp">Teaspoons</option>
            </select>
            <select value={ingredient.size} onChange={(e) => handleIngredientChange(index, "size", e.target.value)}>
              <option value="">Size</option>
              <option value="small">Small</option>
              <option value="medium">Medium</option>
              <option value="large">Large</option>
            </select>
            <input type="text" placeholder="Descriptor (e.g., diced, fresh)" value={ingredient.descriptor} onChange={(e) => handleIngredientChange(index, "descriptor", e.target.value)} />
            <input type="text" placeholder="Item Name" value={ingredient.name} onChange={(e) => handleIngredientChange(index, "name", e.target.value)} required />
            <input type="text" placeholder="Additional Descriptor" value={ingredient.additional_descriptor} onChange={(e) => handleIngredientChange(index, "additional_descriptor", e.target.value)} />
            <button type="button" onClick={() => removeIngredient(index)} style={{ color: "red" }}>X</button>
          </div>
        ))}

        <button type="button" onClick={addIngredient}>+ Add Ingredient</button>
        <textarea name="instructions" placeholder="Instructions" value={recipeForm.instructions} onChange={handleInputChange} />

        <label>Select Parent Recipe (Optional, if this is a Sub-Recipe):</label>
        <select name="parentRecipeId" value={recipeForm.parentRecipeId} onChange={handleInputChange}>
          <option value="">-- No Parent (Regular Recipe) --</option>
          {recipes.map((recipe) => (
            <option key={recipe.id} value={recipe.id}>{recipe.name}</option>
          ))}
        </select>

        <button type="submit">{editingRecipeId ? "Update Recipe" : "Add Recipe"}</button>
      </form>
    </div>
  );
}

export default Recipes;
