// StoreOrganizerFixed.js
import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragOverlay
} from "@dnd-kit/core";
import {
  SortableContext,
  sortableKeyboardCoordinates,
  useSortable,
  verticalListSortingStrategy
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

// Sortable ingredient component
const SortableIngredient = ({ ingredient, index }) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging
  } = useSortable({
    id: `ingredient-${ingredient.id}`,
    data: {
      type: 'ingredient',
      ingredient
    }
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.4 : 1,
    cursor: 'grab',
    userSelect: 'none'
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      className={`ingredient-item ${isDragging ? 'dragging' : ''}`}
      {...attributes}
      {...listeners}
      data-id={ingredient.id}
    >
      {ingredient.name}
    </div>
  );
};

// Section component
const Section = ({ section, activeDroppableId, onRemove }) => {
  // Generate a unique key for the section's sortable context
  const getSectionContextKey = (section) => `section-context-${section.id}-${section.ingredients.length}`;

  return (
    <div
      className={`section-card ${activeDroppableId === `section-${section.id}` ? 'drop-target' : ''}`}
      key={section.id}
      id={`section-${section.id}`}
    >
      <div className="section-header">
        <h3>{section.name}</h3>
        <button
          className="remove-section-btn"
          onClick={() => onRemove(section.id)}
        >
          âœ•
        </button>
      </div>

      <SortableContext
        key={getSectionContextKey(section)}
        id={`section-${section.id}`}
        items={section.ingredients.map(i => `ingredient-${i.id}`)}
        strategy={verticalListSortingStrategy}
      >
        <div
          className="ingredient-list"
          data-section-id={section.id}
        >
          {section.ingredients.length > 0 ? (
            section.ingredients.map((ingredient, index) => (
              <SortableIngredient
                key={`${section.id}-ingredient-${ingredient.id}`}
                ingredient={ingredient}
                index={index}
              />
            ))
          ) : (
            <div className="empty-section">
              Drag ingredients here
            </div>
          )}
        </div>
      </SortableContext>
    </div>
  );
};

// Main component
const StoreOrganizerFixed = () => {
  const navigate = useNavigate();
  const [sections, setSections] = useState([]);
  const [stores, setStores] = useState([]);
  const [selectedStore, setSelectedStore] = useState(null);
  const [newSectionName, setNewSectionName] = useState("");
  const [loading, setLoading] = useState(true);
  const [saveSuccess, setSaveSuccess] = useState(false);
  const [activeId, setActiveId] = useState(null);
  const [activeDragData, setActiveDragData] = useState(null);
  const [activeDroppableId, setActiveDroppableId] = useState(null);

  // Sensors for drag detection
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Load data on startup
  useEffect(() => {
    // Initial data loading
    const fetchData = async () => {
      try {
        setLoading(true);
        
        // Fetch stores
        const storesResponse = await fetch("http://127.0.0.1:5000/api/stores");
        const storesData = await storesResponse.json();
        setStores(storesData);

        if (storesData.length > 0) {
          const storeId = storesData[0].id;
          setSelectedStore(storeId);
          
          // Fetch sections for this store
          await fetchSections(storeId);
        } else {
          // Create a default store
          await createDefaultStore();
        }
      } catch (error) {
        console.error("Error loading data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Debug helper for sections state
  useEffect(() => {
    console.log("ðŸ”„ Sections updated:", sections.map(s => ({
      id: s.id,
      name: s.name,
      ingredients: s.ingredients.map(i => i.id)
    })));
  }, [sections]);

  // Function to fetch sections for a store
  const fetchSections = async (storeId) => {
    try {
      // Get sections
      const sectionsResponse = await fetch(`http://127.0.0.1:5000/api/stores/${storeId}/sections`);
      const sectionsData = await sectionsResponse.json();
      
      // Initialize sections with empty ingredients
      const sectionsWithIngredients = sectionsData.map(section => ({
        ...section,
        ingredients: []
      }));

      // Fetch all ingredients
      const ingredientsResponse = await fetch("http://127.0.0.1:5000/api/ingredients");
      const ingredientsData = await ingredientsResponse.json();
      
      if (ingredientsData.length === 0) {
        setSections(sectionsWithIngredients);
        setLoading(false);
        return;
      }

      // Get ingredient-section mappings
      const mappingsResponse = await fetch(`http://127.0.0.1:5000/api/ingredient_sections?store_id=${storeId}`);
      const mappingsData = await mappingsResponse.json();
      
      // Create section map for easier lookup
      const sectionMap = {};
      sectionsWithIngredients.forEach(section => {
        sectionMap[section.id] = {
          ...section,
          ingredients: []
        };
      });
      
      // Create uncategorized section
      const uncategorizedSection = {
        id: "uncategorized",
        name: "Uncategorized",
        ingredients: []
      };
      
      // Assign ingredients to sections
      ingredientsData.forEach(ingredient => {
        const mapping = mappingsData.find(m => m.ingredient_id === ingredient.id);
        
        if (mapping && sectionMap[mapping.section_id]) {
          sectionMap[mapping.section_id].ingredients.push(ingredient);
        } else {
          uncategorizedSection.ingredients.push(ingredient);
        }
      });
      
      // Build the final sections array
      let finalSections = Object.values(sectionMap);
      
      // Add uncategorized if it has ingredients
      if (uncategorizedSection.ingredients.length > 0) {
        finalSections.push(uncategorizedSection);
      }
      
      console.log("Sections loaded:", finalSections);
      setSections(finalSections);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching sections:", error);
      setLoading(false);
    }
  };

  // Create a default store if none exists
  const createDefaultStore = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/stores", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          name: "My Store",
          sections: [
            { name: "Produce", order: 0 },
            { name: "Dairy", order: 1 },
            { name: "Meat", order: 2 },
            { name: "Bakery", order: 3 },
            { name: "Frozen", order: 4 },
            { name: "Canned Goods", order: 5 },
            { name: "Uncategorized", order: 6 }
          ]
        })
      });

      const data = await response.json();
      
      if (data.store_id) {
        const storesResponse = await fetch("http://127.0.0.1:5000/api/stores");
        const storesData = await storesResponse.json();
        setStores(storesData);
        
        if (storesData.length > 0) {
          setSelectedStore(storesData[0].id);
          await fetchSections(storesData[0].id);
        }
      }
      
      setLoading(false);
    } catch (error) {
      console.error("Error creating default store:", error);
      setLoading(false);
    }
  };

  // Change the selected store
  const handleStoreChange = (e) => {
    const storeId = e.target.value;
    setSelectedStore(storeId);
    fetchSections(storeId);
  };

  // Add a new section
  const addNewSection = () => {
    if (!newSectionName.trim()) {
      alert("Please enter a section name");
      return;
    }

    const newSection = {
      id: `temp-${Date.now()}`,
      name: newSectionName,
      order: sections.length,
      ingredients: []
    };

    setSections([...sections, newSection]);
    setNewSectionName("");
  };

  // Remove a section
  const removeSection = (sectionId) => {
    // Find the section to remove
    const sectionToRemove = sections.find(s => s.id.toString() === sectionId.toString());
    if (!sectionToRemove) return;
    
    // Make a copy of the sections array
    const newSections = [...sections];
    
    // Find or create the uncategorized section
    let uncategorizedIndex = newSections.findIndex(s => s.id === "uncategorized");
    
    if (uncategorizedIndex === -1) {
      // Need to create uncategorized section
      const uncategorized = {
        id: "uncategorized",
        name: "Uncategorized",
        ingredients: [...sectionToRemove.ingredients] // Move ingredients here
      };
      
      // Filter out the section to remove and add uncategorized
      setSections([
        ...newSections.filter(s => s.id.toString() !== sectionId.toString()),
        uncategorized
      ]);
    } else {
      // Update the existing uncategorized section
      const updatedSections = [...newSections];
      
      // Add the ingredients to uncategorized
      updatedSections[uncategorizedIndex] = {
        ...updatedSections[uncategorizedIndex],
        ingredients: [
          ...updatedSections[uncategorizedIndex].ingredients,
          ...sectionToRemove.ingredients
        ]
      };
      
      // Remove the section
      setSections(updatedSections.filter(s => s.id.toString() !== sectionId.toString()));
    }
  };

  // Save the organization to the backend
  const saveOrganization = async () => {
    try {
      // Format sections data for API
      const sectionData = sections
        .filter(section => section.id !== "uncategorized")
        .map(section => ({
          id: section.id.toString().startsWith('temp-') ? null : section.id,
          name: section.name,
          ingredients: section.ingredients.map((ingredient, index) => ({
            id: ingredient.id,
            name: ingredient.name,
            order: index
          }))
        }));

      // Add uncategorized if it exists
      const uncategorizedSection = sections.find(s => s.id === "uncategorized");
      if (uncategorizedSection && uncategorizedSection.ingredients.length > 0) {
        sectionData.push({
          id: null,
          name: "Uncategorized",
          ingredients: uncategorizedSection.ingredients.map((ingredient, index) => ({
            id: ingredient.id,
            name: ingredient.name,
            order: index
          }))
        });
      }

      // Send to API
      const response = await fetch("http://127.0.0.1:5000/api/save_ingredient_sections", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          store_id: selectedStore,
          sections: sectionData
        })
      });

      if (!response.ok) {
        throw new Error("Failed to save organization");
      }

      // Show success message
      setSaveSuccess(true);
      setTimeout(() => setSaveSuccess(false), 3000);
    } catch (error) {
      console.error("Error saving organization:", error);
      alert("Error saving organization: " + error.message);
    }
  };

  // Drag & Drop Handlers
  const handleDragStart = (event) => {
    const { active } = event;
    setActiveId(active.id);
    setActiveDragData(active.data.current?.ingredient || null);
  };

  const handleDragOver = (event) => {
    const { over } = event;
    
    if (!over) {
      setActiveDroppableId(null);
      return;
    }
    
    // Determine proper drop target
    if (over.id.startsWith('section-')) {
      setActiveDroppableId(over.id);
    } else if (over.id.startsWith('ingredient-')) {
      // Find which section this ingredient belongs to
      const ingredientId = over.id.replace('ingredient-', '');
      
      for (const section of sections) {
        if (section.ingredients.some(i => i.id.toString() === ingredientId)) {
          setActiveDroppableId(`section-${section.id}`);
          break;
        }
      }
    }
  };

  const handleDragEnd = (event) => {
    console.log("âš¡ Drag End Event:", event);
    const { active, over } = event;
    
    // Clear states
    setActiveDroppableId(null);
    setActiveId(null);
    setActiveDragData(null);
    
    if (!active || !over) {
      console.log("No valid drag targets");
      return;
    }
    
    console.log("Active:", active.id, "Over:", over.id);
    
    // Exit if no ingredient data
    if (!active.data.current?.ingredient) {
      console.log("No ingredient data found");
      return;
    }
    
    // Get the ingredient being dragged
    const draggedIngredientId = active.id.replace('ingredient-', '');
    
    // Find source section (section containing the dragged ingredient)
    let sourceSectionIndex = -1;
    let sourceIngredientIndex = -1;
    
    for (let i = 0; i < sections.length; i++) {
      const index = sections[i].ingredients.findIndex(
        ing => ing.id.toString() === draggedIngredientId
      );
      
      if (index !== -1) {
        sourceSectionIndex = i;
        sourceIngredientIndex = index;
        break;
      }
    }
    
    if (sourceSectionIndex === -1) {
      console.log("Source section not found");
      return;
    }
    
    // Determine target section
    let targetSectionId;
    let targetSectionIndex;
    
    if (over.id.startsWith('section-')) {
      // Dropped directly on a section
      targetSectionId = over.id.replace('section-', '');
    } else if (over.id.startsWith('ingredient-')) {
      // Dropped on another ingredient, find its section
      const overIngredientId = over.id.replace('ingredient-', '');
      
      for (const section of sections) {
        if (section.ingredients.some(i => i.id.toString() === overIngredientId)) {
          targetSectionId = section.id.toString();
          break;
        }
      }
    } else {
      console.log("Invalid drop target");
      return;
    }
    
    if (!targetSectionId) {
      console.log("Target section not determined");
      return;
    }
    
    // Find the index of the target section
    targetSectionIndex = sections.findIndex(s => s.id.toString() === targetSectionId);
    
    if (targetSectionIndex === -1) {
      console.log("Target section not found in sections array");
      return;
    }
    
    // Create a deep copy of the sections array
    const newSections = JSON.parse(JSON.stringify(sections));
    
    // Get the dragged ingredient (complete object)
    const draggedIngredient = newSections[sourceSectionIndex].ingredients[sourceIngredientIndex];
    
    // Remove from source section
    newSections[sourceSectionIndex].ingredients.splice(sourceIngredientIndex, 1);
    
    // If dropping on an ingredient, determine insertion position
    if (over.id.startsWith('ingredient-')) {
      const overIngredientId = over.id.replace('ingredient-', '');
      const targetIngredientIndex = newSections[targetSectionIndex].ingredients.findIndex(
        i => i.id.toString() === overIngredientId
      );
      
      if (targetIngredientIndex !== -1) {
        // Insert at specific position
        newSections[targetSectionIndex].ingredients.splice(
          targetIngredientIndex, 0, draggedIngredient
        );
      } else {
        // Fallback to adding at the end
        newSections[targetSectionIndex].ingredients.push(draggedIngredient);
      }
    } else {
      // Just add to the end of the target section
      newSections[targetSectionIndex].ingredients.push(draggedIngredient);
    }
    
    console.log("ðŸ”„ Updating sections:", 
      newSections.map(s => ({ 
        id: s.id, 
        name: s.name, 
        ingredients: s.ingredients.map(i => i.id)
      }))
    );
    
    // Update state
    setSections(newSections);
  };

  // Debug utilities
  const inspectSections = () => {
    console.log("ðŸ“Š SECTION INSPECTION:");
    sections.forEach(section => {
      console.log(`Section ${section.name} (${section.id}): ${section.ingredients.length} ingredients`);
      const ingredientIds = section.ingredients.map(i => i.id);
      console.log("Ingredient IDs:", ingredientIds);
      
      // Check for duplicates
      const uniqueIds = new Set(ingredientIds);
      if (uniqueIds.size !== ingredientIds.length) {
        console.error("âš ï¸ DUPLICATE INGREDIENTS DETECTED");
      }
    });
  };

  return (
    <div className="store-organizer-container">
      <div className="store-organizer-header">
        <h1>Store Section Organizer</h1>
        {saveSuccess && <div className="save-success-message">Organization saved successfully!</div>}
      </div>

      <div className="store-organizer-controls">
        <button
          className="back-btn"
          onClick={() => navigate('/grocery-list')}
        >
          â† Back to Grocery List
        </button>

        <div className="store-selector">
          <label htmlFor="store-select">Select Store:</label>
          <select
            id="store-select"
            value={selectedStore || ""}
            onChange={handleStoreChange}
          >
            {stores.map(store => (
              <option key={store.id} value={store.id}>{store.name}</option>
            ))}
          </select>
        </div>

        <div className="add-section-form">
          <input
            type="text"
            value={newSectionName}
            onChange={(e) => setNewSectionName(e.target.value)}
            placeholder="New Section Name"
          />
          <button onClick={addNewSection}>Add Section</button>
        </div>

        <button className="save-organization-btn" onClick={saveOrganization}>
          Save Organization
        </button>
      </div>

      {loading ? (
        <div className="loading">Loading sections and ingredients...</div>
      ) : (
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragStart={handleDragStart}
          onDragOver={handleDragOver}
          onDragEnd={handleDragEnd}
        >
          <div className="organizer-layout">
            <div className="ingredients-panel">
              <h2>Uncategorized Ingredients</h2>
              <SortableContext
                id="all-ingredients"
                items={sections
                  .find(s => s.id === "uncategorized")?.ingredients.map(i => `ingredient-${i.id}`) || []}
                strategy={verticalListSortingStrategy}
              >
                <div className="ingredient-list all-ingredients">
                  {sections.find(s => s.id === "uncategorized")?.ingredients.map((ingredient, index) => (
                    <SortableIngredient
                      key={`uncategorized-ingredient-${ingredient.id}`}
                      ingredient={ingredient}
                      index={index}
                    />
                  ))}
                  {!sections.find(s => s.id === "uncategorized") ||
                    sections.find(s => s.id === "uncategorized").ingredients.length === 0 ? (
                    <div className="empty-ingredients">
                      No uncategorized ingredients
                    </div>
                  ) : null}
                </div>
              </SortableContext>
            </div>

            <div className="sections-container">
              {sections.filter(section => section.id !== "uncategorized").map(section => (
                <Section 
                  key={section.id}
                  section={section}
                  activeDroppableId={activeDroppableId}
                  onRemove={removeSection}
                />
              ))}
            </div>
          </div>

          <DragOverlay>
            {activeId && activeDragData ? (
              <div className="ingredient-item dragging">
                {activeDragData.name}
              </div>
            ) : null}
          </DragOverlay>
        </DndContext>
      )}

      <div className="organizer-tip">
        <p>Tip: Drag and drop ingredients between sections to organize your grocery list.</p>
      </div>
      
      <div style={{ marginTop: '20px', padding: '10px', background: '#f0f0f0', borderRadius: '4px' }}>
        <h3>Debug Info:</h3>
        <button onClick={() => console.log("Current sections:", sections)}>
          Log Sections State
        </button>
        <button onClick={inspectSections} style={{ marginLeft: '10px' }}>
          Inspect Sections
        </button>
        <div style={{ fontSize: '12px', marginTop: '10px' }}>
          Active ID: {activeId || 'none'}
        </div>
      </div>
    </div>
  );
};

export default StoreOrganizerFixed;
